<span class="token punctuation">}</span>

<span class="token comment">/* table-header-group 表格 header */</span>
<span class="token selector">.table section:nth-of-type(1)</span> <span class="token punctuation">{</span>
    <span class="token property">font-weight</span><span class="token punctuation">:</span> bold<span class="token punctuation">;</span>
    <span class="token property">display</span><span class="token punctuation">:</span> table-header-group<span class="token punctuation">;</span>
    <span class="token property">background</span><span class="token punctuation">:</span> #555<span class="token punctuation">;</span>
    <span class="token property">color</span><span class="token punctuation">:</span> white<span class="token punctuation">;</span>
<span class="token punctuation">}</span>

<span class="token comment">/* table-row-group 表格 body */</span>
<span class="token selector">.table section:nth-of-type(2)</span> <span class="token punctuation">{</span>
    <span class="token property">display</span><span class="token punctuation">:</span> table-row-group<span class="token punctuation">;</span>
<span class="token punctuation">}</span>
<span class="token comment">/* table-footer-group 表格 footer */</span>
<span class="token selector">.table section:nth-of-type(3)</span> <span class="token punctuation">{</span>
    <span class="token property">display</span><span class="token punctuation">:</span> table-footer-group<span class="token punctuation">;</span>
    <span class="token property">background</span><span class="token punctuation">:</span> #f3f3f3<span class="token punctuation">;</span>
<span class="token punctuation">}</span>

<span class="token comment">/* table-row 表格 行 */</span>
<span class="token selector">.table section ul</span> <span class="token punctuation">{</span>
